import React, { useState, useEffect } from 'react';
import { Link } from 'react-scroll';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [activeSection, setActiveSection] = useState('home');

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  const handleSetActive = (section) => {
    setActiveSection(section);
    closeMenu();
  };

  return (
    <header className="l-header">
      <nav className="nav bd-grid">
        <div>
          <Link to="home" className="nav__logo">Prajwal</Link>
        </div>

        <div className={`nav__menu ${isMenuOpen ? 'show' : ''}`} id="nav-menu">
          <ul className="nav__list">
            <li className="nav__item">
              <Link
                to="home"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
                className={`nav__link ${activeSection === 'home' ? 'active' : ''}`}
                onSetActive={() => handleSetActive('home')}
                onClick={closeMenu}
              >
                Home
              </Link>
            </li>
            <li className="nav__item">
              <Link
                to="about"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
                className={`nav__link ${activeSection === 'about' ? 'active' : ''}`}
                onSetActive={() => handleSetActive('about')}
                onClick={closeMenu}
              >
                About
              </Link>
            </li>
            <li className="nav__item">
              <Link
                to="skills"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
                className={`nav__link ${activeSection === 'skills' ? 'active' : ''}`}
                onSetActive={() => handleSetActive('skills')}
                onClick={closeMenu}
              >
                Skills
              </Link>
            </li>
            <li className="nav__item">
              <Link
                to="projects"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
                className={`nav__link ${activeSection === 'projects' ? 'active' : ''}`}
                onSetActive={() => handleSetActive('projects')}
                onClick={closeMenu}
              >
                Projects
              </Link>
            </li>
            <li className="nav__item">
              <Link
                to="contact"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
                className={`nav__link ${activeSection === 'contact' ? 'active' : ''}`}
                onSetActive={() => handleSetActive('contact')}
                onClick={closeMenu}
              >
                Contact
              </Link>
            </li>
          </ul>
        </div>

        <div className="nav__toggle" id="nav-toggle" onClick={toggleMenu}>
          <i className='bx bx-menu'></i>
        </div>
      </nav>
    </header>
  );
};

export default Header;
